  //   redirectTo: ""
  // }

  // componentDidMount(){
  //   const {name, email, phone} = this.props.location.state.contact
  //   this.setState({
  //     name, email, phone
  //   })
  // }

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }
  contactDeleted = contact=> {
    this.setState(prevState => ({
